    margin-bottom: 6px;
    font-size: 0.9em;
    text-transform: uppercase;
}
.status-item-pro-v20 span {
    color: #ffffff;
    font-size: 1.1em;
    font-weight: 500;
    font-family: 'Consolas', 'Courier New', monospace;
}
.status-item-pro-v20 .highlight-pro-v20 {
    color: #43b581; /* Discord Green */
    text-shadow: 0 0 8px rgba(67, 181, 129, 0.7);
}
.status-item-pro-v20 .critical-pro-v20 {
    color: #f04747; /* Discord Red */
    text-shadow: 0 0 8px rgba(240, 71, 71, 0.7);
}
</style>
