        <!-- 信息显示框 -->
        <div id="info-box">
            <h3 id="info-title"></h3>
            <p id="info-content"></p>
        </div>
    </div>

    <script>
        const landmarks = document.querySelectorAll('.landmark');
        const infoBox = document.getElementById('info-box');
        const infoTitle = document.getElementById('info-title');
        const infoContent = document.getElementById('info-content');

        landmarks.forEach(landmark => {
            const tooltip = document.createElement('span');
            tooltip.classList.add('tooltip');
            tooltip.textContent = landmark.getAttribute('data-name');
            landmark.appendChild(tooltip);

            landmark.addEventListener('click', (event) => {
                event.stopPropagation();

                const name = landmark.getAttribute('data-name');
                const info = landmark.getAttribute('data-info');

                infoTitle.textContent = name;
                infoContent.textContent = info;
                infoBox.style.display = 'block';
            });
        });

        document.getElementById('map-container').addEventListener('click', () => {
             if (infoBox.style.display !== 'none') {
                infoBox.style.display = 'none';
            }
        });
    </script>
</body>
</html>
